mapper.select:
SELECT *
FROM appl_ansr
WHERE quiz_no = #{quizNo}
  AND exam_no = #{exzmNo}
  AND std_no = #{stdNo}

mapper.update:
UPDATE appl_ansr

	ord = #{ord},
	ansr = #{ansr},
	scor = #{scor},
	ansr_yn = #{ansrYn},

WHERE quiz_no = #{quizNo}
  AND exam_no = #{exzmNo}
  AND std_no = #{stdNo}

mapper.delete:
DELETE FROM appl_ansr
WHERE quiz_no = #{quizNo}
  AND exam_no = #{exzmNo}
  AND std_no = #{stdNo}
